const state = {
  item: [
    {
      id: 1,
      title: "abc",
    },
    {
      id: 2,
      title: "xyz",
    },
    {
      id: 2,
      title: "hsdb",
    },
  ],
};
const getters = {
    allItem : state => state.item
};
const actions = {};

const mutations = {};

export default {
  state,
  getters,
  actions,
  mutations,
};
