import axios from "axios";

const state = {
  users: [],
};
const getters = {
  allUsers: (state) => state.products,
};
const actions = {
  async allUsers({commit}, product){
    const response = await axios.post("http://localhost:3000/users", product);
    commit("newUser", response.data)
  }
};

const mutations = {
    newUser: (state,  user) => (state.products.unshift(product)),
};

export default {
  state,
  getters,
  actions,
  mutations,
};
