import axios from "axios";

const state = {
  users: [],
};
const getters = {
  allUsers: (state) => state.products,
};
const actions = {
  async allUsers({commit}, user){
    const response = await axios.post("http://localhost:3000/users", user);
    commit("newUser", response.data)
    console.log(response)
    if (response.status == 201) {
        localStorage.setItem("user-info", JSON.stringify(response.data));
      }
  },
  async allUsers({commit}){
      const response= await axios.get(`http://localhost:3000/users?email=${this.email}&password=${this.password}`);
      commit("newLogin", response.data)
  }
};

const mutations = {
    newUser: (state,  user) => (state.users.unshift(user)),
};

export default {
  state,
  getters,
  actions,
  mutations,
};
