import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    products: [
      {
        id: 1,
        title: "abv",
        price: 300,
      },
      {
        id: 2,
        title: "abvf",
        price: 3002,
      },
    ]
  },
  getters: {
    allProducts: state => state.products

  },
  mutations: {
  },
  actions: {

  },
  modules: {

  }
})
